js:
var res = {};
var items = [];
var origin_url = getUrl();
var json = JSON.parse(getResCode());
for (var i = 0; i < json.data.list.length; i++) {
    var data = json.data.list[i];
    var desc = '';
    var url = data.playUrl;
    if (data.channelId == 1) {
        desc = (data.hasOwnProperty("score") ? data.score + "分\t" : '');
    } else if (data.channelId == 2 || data.channelId == 4) {
        if (data.latestOrder == data.videoCount) {
            desc = (data.hasOwnProperty("score") ? data.score + "分\t" : '') + data.latestOrder + "集全";
        } else {
            desc = (data.hasOwnProperty("score") ? data.score + "分\t" : '') + data.latestOrder + "/" + data.videoCount + "集";
        }
    } else {
        desc = data.formatPeriod + "期";
    }
    if (data.albumId != null) {
        if (data.sourceId != 0) {
            url = 'https://pcw-api.iqiyi.com/album/source/svlistinfo?sourceid=' + data.albumId + '&timelist=all&url=' + data.playUrl;
        } else {
            url = 'https://pcw-api.iqiyi.com/albums/album/avlistinfo?aid=' + data.albumId + '&size=24&page=0&url=' + data.playUrl;
        }
    }
    items.push({
        title: data.name,
        desc: desc,
        pic_url: data.imageUrl,
        url: url
    });
}
res.data = items;
setHomeResult(res);

