var res = {};
var items = [];
var origin_url = getUrl();
eval(fetch('hiker://files/rules/js/g-parse-list.js',{}));
function toUrl(mode, url) {
    if (typeof mode === 'number') {
        if (mode === 0) {
            return url + `@rule=js:var res = {};var items = [];var origin_url = getUrl();var parse_list = ["` + parse_list.join('", "') + `"];;var parsing_list = ["` + parsing_list.join('", "') + `"];items.push({ title: '直链解析合集', col_type: 'text_1' });for(var i=0;i<parse_list.length;i++){items.push({title:parse_list[i].split('￥')[0],url:parse_list[i].split('￥')[1]+origin_url+parse_list[i].split('￥')[2],col_type:'text_2'})}items.push({ title: '网页解析合集', col_type: 'text_1' });for(var i=0;i<parsing_list.length;i++){items.push({title:parsing_list[i].split('￥')[0],url:parsing_list[i].split('￥')[1]+origin_url,col_type:'text_3'})}res.data = items;setHomeResult(res);`;
        } else if (mode <= parse_list.length) {
            return parsing_list[mode - 1].split('￥')[1] + url;
        }
    } else if (typeof mode === 'string') {
        for (var key in parse_list) {
            if (parse_list[key].indexOf(mode) > -1) {
                return parse_list[key].split('￥')[1] + url + parse_list[key].split('￥')[2];
            }
        }
    }
    return url;
}
if (origin_url.indexOf('api') != -1) {
    var json = JSON.parse(getResCode());
    var list = json.data.epsodelist;
    var col_type = 'movie_2';
    if (origin_url.indexOf('timelist') != -1 && json.code == 'A00000' && json.data.all.length != 0) {
        list = json.data.all;
        col_type = 'movie_1';
    } else if (json.code == 'A00000' && json.data.epsodelist.length != 0) {
        list = json.data.epsodelist;
        if (json.data.latestOrder < 50 && list[0].name.length > 11) {
            col_type = 'movie_1';
        }
    }
    if (list.length > 0) {
        items.push({
            title: '直链解析合集',
            url: origin_url + `@rule=js:var res={};var items=[];var json = JSON.parse(getResCode());var list = (getUrl().indexOf('timelist')!=-1?json.data.all:json.data.epsodelist);var parse_list=["` + parse_list.join('","') + `"];for(var i=0;i<parse_list.length;i++){items.push({title:parse_list[i].split('￥')[0]+' ==>',col_type:'text_1'});for(var j=0;j<list.length;j++){items.push({title:list[j].order!=0?list[j].order:list[j].period,url:parse_list[i].split('￥')[1]+list[j].playUrl+parse_list[i].split('￥')[2],col_type:list[j].order!=0?'text_4':'text_3'})}}res.data=items;setHomeResult(res);`,
            col_type: 'text_2'
        });
        items.push({
            title: '网页解析合集',
            url: origin_url + `@rule=js:var res={};var items=[];var json = JSON.parse(getResCode());var list = (getUrl().indexOf('timelist')!=-1?json.data.all:json.data.epsodelist);var parsing_list=["` + parsing_list.join('","') + `"];for(var i=0;i<parsing_list.length;i++){items.push({title:parsing_list[i].split('￥')[0]+' ==>'.split('&')[0],col_type:'text_1'});for(var j=0;j<list.length;j++){items.push({title:list[j].order!=0?list[j].order:list[j].period,url:parsing_list[i].split('￥')[1]+list[j].playUrl,col_type:list[j].order!=0?'text_4':'text_3'})}}res.data=items;setHomeResult(res);`,
            col_type: 'text_2'
        });
        for (var i = 0; i < list.length; i++) {
            var data = list[i];
            items.push({
                title: data.name,
                pic_url: data.imageUrl,
                url: toUrl(iqiyi_mode, data.playUrl),
                desc: (data.hasOwnProperty('subtitle') ? data.subtitle : '') + '\n' + data.period,
                col_type: col_type
            });
        }
    }
} else {
    items.push({
        title: '直链解析',
        col_type: 'text_1'
    });
    for (var i = 0; i < parse_list.length; i++) {
        items.push({
            title: parse_list[i].split('￥')[0],
            url: parse_list[i].split('￥')[1] + origin_url + parse_list[i].split('￥')[2],
            col_type: 'text_2'
        });
    }
    items.push({
        title: '网页解析',
        col_type: 'text_1'
    });
    for (var i = 0; i < parsing_list.length; i++) {
        items.push({
            title: parsing_list[i].split('￥')[0],
            url: parsing_list[i].split('￥')[1] + origin_url,
            col_type: 'text_3'
        });
    }
}
res.data = items;
setHomeResult(res);

