eval(fetch('hiker://files/rules/js/g-parse-list.js', {}));
function toUrl(mode, url) {
    if (typeof mode === 'number') {
        if (mode === 0) {
            return url + `@rule=js:var res = {};var items = [];var origin_url = getUrl();var parse_list = ["` + parse_list.join('", "') + `"];;var parsing_list = ["` + parsing_list.join('", "') + `"];items.push({ title: '直链解析合集', col_type: 'text_1' });for(var i=0;i<parse_list.length;i++){items.push({title:parse_list[i].split('￥')[0],url:parse_list[i].split('￥')[1]+origin_url+parse_list[i].split('￥')[2],col_type:'text_2'})}items.push({ title: '网页解析合集', col_type: 'text_1' });for(var i=0;i<parsing_list.length;i++){items.push({title:parsing_list[i].split('￥')[0],url:parsing_list[i].split('￥')[1]+origin_url,col_type:'text_3'})}res.data = items;setHomeResult(res);`;
        } else if (mode <= parse_list.length) {
            return parsing_list[mode - 1].split('￥')[1] + url;
        }
    } else if (typeof mode === 'string') {
        for (var key in parse_list) {
            if (parse_list[key].indexOf(mode) > -1) {
                return parse_list[key].split('￥')[1] + url + parse_list[key].split('￥')[2];
            }
        }
    }
    return url;
}
if (video_lists.length == 1) {
    items.push({
        title: '直链解析',
        col_type: 'text_1'
    });
    for (var i = 0; i < parse_list.length; i++) {
        items.push({
            title: parse_list[i].split('￥')[0],
            url: parse_list[i].split('￥')[1] + url + parse_list[i].split('￥')[2],
            col_type: 'text_2'
        });
    }
    items.push({
        title: '网页解析',
        col_type: 'text_1'
    });
    for (var i = 0; i < parsing_list.length; i++) {
        items.push({
            title: parsing_list[i].split('￥')[0],
            url: parsing_list[i].split('￥')[1] + url,
            col_type: 'text_3'
        });
    }
} else if (video_lists.length > 1) {
    for (var i = 0; i < video_lists.length; i += 30) {
        video_list.push(video_lists.slice(i, i + 30));
    }
    var list = [];
    for (var i = 0; i < video_list.length; i++) {
        var o_url = 'https://union.video.qq.com/fcgi-bin/data?otype=json&tid=682&appid=20001238&appkey=6c03bbe9658448a4&union_platform=1&idlist=' + video_list[i].join(',');
        eval(fetch(o_url, {}));
        var col_type = QZOutputJson.results[0].fields.title.length > 12 ? 'movie_1' : 'movie_2';
        for (var j = 0; j < QZOutputJson.results.length; j++) {
            var data = QZOutputJson.results[j].fields;
