                pic_url: data.pic160x90,
                desc: data.video_checkup_time,
                url: toUrl(qq_mode, url),
                col_type: col_type
            });
        }
    }
    items.unshift({
        title: "网页解析合集",
        url: origin_url + `@rule=js:var res={};var items=[];var list = ["` + list.join('","') + `"];var parsing_list=["` + parsing_list.join('","') + `"];for(var i=0;i<parsing_list.length;i++){items.push({title:parsing_list[i].split('￥')[0]+' ==>',col_type:'text_1'});for(var j=0;j<list.length;j++){items.push({title:list[j].split('￥')[0],url:parsing_list[i].split('￥')[1]+list[j].split('￥')[1],col_type:((list[j].split('￥')[0].length)>10?'text_1':'text_2')})}}res.data=items;setHomeResult(res);`,
        col_type: 'text_2'
    });
    items.unshift({
        title: "直链解析合集",
        url: origin_url + `@rule=js:var res={};var items=[];var list = ["` + list.join('","') + `"];var parse_list=["` + parse_list.join('","') + `"];for(var i=0;i<parse_list.length;i++){items.push({title:parse_list[i].split('￥')[0]+' ==>',col_type:'text_1'});for(var j=0;j<list.length;j++){items.push({title:list[j].split('￥')[0],url:parse_list[i].split('￥')[1]+list[j].split('￥')[1]+parse_list[i].split('￥')[2],col_type:((list[j].split('￥')[0].length)>10?'text_1':'text_2')})}}res.data=items;setHomeResult(res);`,
        col_type: 'text_2'
    });
} else {
    items.push({
        title: '原网页',
        url: 'https://v.qq.com/x/cover/' + cid + '.html',
        col_type: 'text_center_1'
    });
}
res.data = items;
setHomeResult(res);

