js:
const _config = {
  home_name: "AGE动漫",
  remote_update: 'https://gitee.com/quiet_boy/hiker/raw/master/agefans/update.json',
  local_js: 'hiker://files/quietboy/agefans.js',
  local_config: true,
  local_config_path: 'hiker://files/quietboy/agefans_config.json',
}

function queryUpdate() {
  if (getUrl().indexOf('ppuqq') >= 0) {
    return true;
  }
  else {
    return false;
  }
}
function queryConfig() {
  if (!_config.local_config) {
    return false;
  }
  if (getUrl().indexOf('ppcqq') >= 0) {
    return true;
  }
  else {
    return false;
  }
}
function getLocalVersion(name) {
  let home = fetch('hiker://home', {});
  let version = -1;
  homeJson = JSON.parse(home);
  homeJson.some(i => {
    if (i.title == name) {
      version = i.version;
      return true;
    }
    else {
      return false;
    }
  });
  return version;
}
function getLocalJS() {
  let jsCode = fetch(_config.local_js, {});
  return jsCode;
}
function getRemoteCommandRoute(remote_command) {
  return fetch(remote_command, {});
}
function displayUpdate() {
  let res = {};
  let localVersion = getLocalVersion(_config.home_name);
  let remoteJson = JSON.parse(fetch(_config.remote_update, {}));
  let remoteVersion = remoteJson.version;
  let remoteCMD = remoteJson.command;
  let remoteJS = remoteJson.remote;
  let remoteConfig = JSON.stringify(remoteJson.config);
  d = [{
    title: '本地版本:V' + localVersion + '    远程版本:V' + remoteVersion,
    desc: '点击更新规则，若无效请联系作者修复',
    url: `hiker://empty@lazyRule=.js:` +
      `writeFile('` + _config.local_js + `', fetch('` + remoteJS + `', {}));` +
      ((_config.local_config) ? (`writeFile('` + _config.local_config_path + `','` + remoteConfig + `');`) : (``)) +
      `'` + getRemoteCommandRoute(remoteCMD) + `'`,
    col_type: 'text_1'
  }, {
    title: '<p style="text-align: center;"><strong><font color="red">·更新日志·</font></strong><ul><li>' + remoteJson.note.join('</li><li>') + '</li></ul></p>',
    col_type: 'rich_text'
  }];
  res.data = d;
  setHomeResult(res);
}
function displayConfig() {
  function findOptTitle(opts, value) {
    let title = '';
    opts.some((item) => {
      if (item.value == value) {
        title = item.title;
        return true;
      }
      else {
        return false;
      }
    });
    return title;
  }
  let res = {};
  let d = [];
  let Config = JSON.parse(getVar('Config'));
  for (let i = 0; i < Config.length; i++) {
    d.push({
      title: Config[i].title + ': ‘‘' + findOptTitle(Config[i].opts, Config[i].value) + '’’',
      col_type: 'text_1'
    });
    for (let j = 0; j < Config[i].opts.length; j++) {
      d.push({
        title: Config[i].opts[j].title,
        col_type: 'text_3',
        url: `hiker://empty@lazyRule=.js:let Config = JSON.parse(getVar('Config'));Config[` + i + `].value=Config[` + i + `].opts[` + j + `].value;writeFile('` + _config.local_config_path + `',JSON.stringify(Config));refreshPage();'toast://修改成功！'`
      });
    }
  }
  res.data = d;
  setHomeResult(res);
}
try {
  if (queryUpdate()) {
    displayUpdate();
  }
  else if (queryConfig()) {
    displayConfig();
  }
  else {
    var localJS = getLocalJS();
    if (localJS == '') {
      throw 'none';
    }
    else {
      eval(localJS);
      jiexi();
    }
  }
} catch (e) {
  displayUpdate();
}

