                    d.push({
                        col_type: 'line_blank'
                    });
                }
                d.push({
                    title: ings
                });
                for(let i = 0; i < model_line_height; i++) {
                    d.push({
                        col_type: 'line_blank'
                    });
                }
            } else {
                d.push({
                    title: getBlockquoteText(author + desc) + (showVideo === '' ? '' : showVideo + '<br><br>') + ings
                });
            }
        } catch (e) { }

        /**
         * 正文
         */
        try {
            let stepsTitle = getBigText(getTitleText('' +  parseDomForHtml(mainHtml, '#steps&&Text') + ''));
            let stepsHtmlArray = parseDomForArray(mainHtml, '.steps&&.container');
            if(multiStepModel && model_line_height > 0) {
                d.push({
                    title: stepsTitle
                });
            }
            let steps = '';
            for (let i = 0; i < stepsHtmlArray.length; i++) {
                let step = parseDomForHtml(stepsHtmlArray[i], '.container&&Html');
                step = getStrongText('步骤' +(i + 1) + '<br>')
                    + step
                        .replace(/<\/p> <img src=/g, '<br><img src=')
                        .replaceWithCallback(/(<img (src|data-src)="(.*?)"([\s\S]*?)>)/g, "<img data-v>", imgTagReplacement2)
                        .replace(/(<p class="text" style=".*?">)|(<\/p>)/, '');
                if(multiStepModel && model_line_height > 0) {
                    d.push({
                        title: step
                    })
                } else {
                    steps += '<br><br>' + step;
                }
            }
            if (!(multiStepModel && model_line_height > 0)) {
                d.push({
                    title: stepsTitle + steps
                });
            }
            for(let i = 0; i < model_line_height; i++) {
                d.push({
                    col_type: 'line_blank'
                });
            }
            /**
