                let tipsTitle = getBigText(getTitleText(parseDomForHtml(tipsHtml, 'h2&&Text')));
                tipsContent = '<br>' + parseDomForHtml(tipsHtml, '.tip&&Html');
                tips = getColorText(tipsTitle + tipsContent, tipsColor);
                d.push({
                    title: tips
                });
                for(let i = 0; i < model_line_height; i++) {
                    d.push({
                        col_type: 'line_blank'
                    });
                }
            } catch(e) { }
        } catch (e) { }

        /**
