                let commentsBlockHtmlArray = parseDomForArray(commentsHtmlArray[i], 'li&&.avatar-right-2');
                for (let j = 0; j < commentsBlockHtmlArray.length; j++) {
                    let replyComment = '';
                    let authorComment = '';
                    if (commentsBlockHtmlArray[j].indexOf('赞') > -1) {
                        let replier = getAuthorText(parseDomForHtml(commentsBlockHtmlArray[j], '.info&&a&&Text'));
                        let reply_time = '';
                        if (/(<span class="normal-font gray-font align-baseline">[\s\S]*?<\/span>)/.test(commentsBlockHtmlArray[j]) === true) {
                            reply_time = getGreyText(RegExp.$1);
                        }
                        let reply_meta = getCommentMeta('<br>' + replier + reply_time);
                        let reply_content = parseDomForHtml(commentsBlockHtmlArray[j], '.right-bottom&&Html');
                        replyComment = reply_content + reply_meta;
                    }
                    if (commentsBlockHtmlArray[j].indexOf('作者回复') > -1) {
                        try {
                            let author = getAuthorText('作者回复');
                            let time = '';
                            if (/(<span class="normal-font gray-font align-baseline">[\s\S]*?<\/span>)/.test(commentsBlockHtmlArray[j]) === true) {
                                time = getGreyText(RegExp.$1);
                            }
                            let meta = getCommentMeta('<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;' + author + time);
                            let commentContent = '<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>'
